```java:src/main/java/com/bsec/bsec/LoginController.java, lines=ALL(1-186)
package com.bsec.bsec;

import javafx.fxml.FXML;
import javafx.fxml.FXMLLoader;
import javafx.fxml.Initializable;
import javafx.scene.Parent;
import javafx.scene.Scene;
import javafx.scene.control.Alert;
import javafx.scene.control.ChoiceBox;
import javafx.scene.control.Label;
import javafx.scene.control.PasswordField;
import javafx.scene.control.TextField;
import javafx.stage.Stage;

import java.io.IOException;
import java.net.URL;
import java.util.ResourceBundle;

public class LoginController implements Initializable {
 @FXML
 private Label welcomeText;

 @FXML
 private TextField Id_number;

 @FXML
 private ChoiceBox<String> user_type;

 @FXML
 private PasswordField PasswordField;

 @Override
 public void initialize(URL url, ResourceBundle resourceBundle) {
 // Populate user types
 user_type.getItems().addAll(
 "Investor",
 "Stock Exchange",
 "Broker",
 "Stock Analyst",
 "Company",
 "Auditor",
 "BSEC Officer",
 "Regulator"
 );
 }

 @FXML
 protected void onHelloButtonClick() {
 welcomeText.setText("Welcome to JavaFX Application!");
 }

 @FXML
 protected void SubmitButton() {
 String idText = Id_number.getText();
 String passwordText = PasswordField.getText();
 String selectedUserType = user_type.getValue();

 // Validate ID (must be 6 digits)
 if (idText == null || !idText.matches("\\d{6}")) {
 showAlert("Invalid ID", "ID must be exactly 6 digits.");
 return;
 }

 // Validate password (must be 6 characters)
 if (passwordText == null || passwordText.length() != 6) {
 showAlert("Invalid Password", "Password must be exactly 6 characters (letters, digits, or combined).");
 return;
 }

 // Validate user type selection
 if (selectedUserType == null) {
 showAlert("User Type Required", "Please select a user type.");
 return;
 }

 // Authenticate user with database
 User authenticatedUser = DatabaseManager.authenticateUser(idText, passwordText, selectedUserType);

 if (authenticatedUser != null) {
 showAlert("Login Successful", "Welcome " + authenticatedUser.getFullName() + "!\n" +
 "User ID: " + authenticatedUser.getUserId() + "\n" +
 "User Type: " + authenticatedUser.getUserType());

 // Clear form after successful login
 Id_number.clear();
 PasswordField.clear();
 user_type.setValue(null);

 // Navigate to appropriate user type page
 navigateToUserTypePage(selectedUserType);
 } else {
 showAlert("Login Failed", "Invalid credentials. Please check your ID, password, and user type.\n\n" +
 "If you don't have an account, please sign up first.");
 }
 }

 @FXML
 protected void SignUpButton() {
 try {
 // Load the SignUp FXML
 FXMLLoader loader = new FXMLLoader(getClass().getResource("SignUp.fxml"));
 Parent signUpRoot = loader.load();

 // Get current stage
 Stage currentStage = (Stage) Id_number.getScene().getWindow();

 // Create new scene and set it
 Scene signUpScene = new Scene(signUpRoot);
 currentStage.setScene(signUpScene);
 currentStage.setTitle("BSEC Sign Up");

 } catch (IOException e) {
 showAlert("Error", "Could not load Sign Up page: " + e.getMessage());
 }
 }

 private void navigateToUserTypePage(String userType) {
 try {
 String fxmlPath = "";
 String windowTitle = "";

 // Map user types to their respective FXML files
 switch (userType) {
 case "Investor":
 fxmlPath = "Investor/Main.fxml";
 windowTitle = "BSEC - Investor Dashboard";
 break;
 case "Stock Exchange":
 fxmlPath = "StockExchange/Main.fxml";
 windowTitle = "BSEC - Stock Exchange Dashboard";
 break;
 case "Broker":
 fxmlPath = "Broker/Main.fxml";
 windowTitle = "BSEC - Broker Dashboard";
 break;
 case "Stock Analyst":
 fxmlPath = "StockAnalyst/Main.fxml";
 windowTitle = "BSEC - Stock Analyst Dashboard";
 break;
 case "Company":
 fxmlPath = "Company/Main.fxml";
 windowTitle = "BSEC - Company Dashboard";
 break;
 case "Auditor":
 fxmlPath = "Auditor/Main.fxml";
 windowTitle = "BSEC - Auditor Dashboard";
 break;
 case "BSEC Officer":
 fxmlPath = "BSEC_Officer/Main.fxml";
 windowTitle = "BSEC - Officer Dashboard";
 break;
 case "Regulator":
 fxmlPath = "Regulator/Main.fxml";
 windowTitle = "BSEC - Regulator Dashboard";
 break;
 default:
 showAlert("Error", "Unknown user type: " + userType);
 return;
 }

 // Load the user type FXML
 FXMLLoader loader = new FXMLLoader(getClass().getResource(fxmlPath));
 Parent userTypeRoot = loader.load();

 // Get current stage
 Stage currentStage = (Stage) Id_number.getScene().getWindow();

 // Create new scene and set it
 Scene userTypeScene = new Scene(userTypeRoot);
 currentStage.setScene(userTypeScene);
 currentStage.setTitle(windowTitle);

 } catch (IOException e) {
