 protected void loginAsBroker() {
 if (authenticateUser("Broker")) {
 navigateToUserTypePage("Broker", "Broker/Menu_Investor.fxml", "BSEC - Broker Dashboard");
 }
 }

 @FXML
 protected void loginAsStockAnalyst() {
 if (authenticateUser("Stock Analyst")) {
 navigateToUserTypePage("Stock Analyst", "StockAnalyst/Menu_Investor.fxml", "BSEC - Stock Analyst Dashboard");
 }
 }

