 protected void loginAsCompany() {
 if (authenticateUser("Company")) {
 navigateToUserTypePage("Company", "Company/Menu_Investor.fxml", "BSEC - Company Dashboard");
 }
 }

 @FXML
 protected void loginAsAuditor() {
 if (authenticateUser("Auditor")) {
 navigateToUserTypePage("Auditor", "Auditor/Menu_Investor.fxml", "BSEC - Auditor Dashboard");
 }
 }

 @FXML
 protected void loginAsBSECOfficer() {
