+ private void setupEventHandlers() {
+ quantityField.textProperty().addListener((obs, oldVal, newVal) -> {
+ if (!newVal.matches("\\d*")) {
+ quantityField.setText(newVal.replaceAll("[^\\d]", ""));
+ }
+ calculateTotal();
+ });
+
+ priceLimitField.textProperty().addListener((obs, oldVal, newVal) -> {
+ calculateTotal();
+ });
