 public void initialize(URL url, ResourceBundle resourceBundle) {
 // Populate user types
 user_type.getItems().addAll(
 "Investor",
 "Stock Exchange",
 "Broker",
 "Stock Analyst",
 "Company",
 "Auditor",
 "BSEC Officer",
 "Regulator"
 );
