 }
 }

 @FXML
 protected void loginAsAuditor() {
 if (authenticateUser("Auditor")) {
 navigateToUserTypePage("Auditor", "Auditor/Menu_Investor.fxml", "BSEC - Auditor Dashboard");
 }
 }

