+ fxmlPath = "Company/Main.fxml";
+ windowTitle = "BSEC - Company Dashboard";
+ break;
+ case "Auditor":
+ fxmlPath = "Auditor/Main.fxml";
+ windowTitle = "BSEC - Auditor Dashboard";
+ break;
+ case "BSEC Officer":
+ fxmlPath = "BSEC_Officer/Main.fxml";
+ windowTitle = "BSEC - Officer Dashboard";
+ break;
+ case "Regulator":
+ fxmlPath = "Regulator/Main.fxml";
+ windowTitle = "BSEC - Regulator Dashboard";
+ break;
+ default:
+ showAlert("Error", "Unknown user type: " + userType);
+ return;
+ }
+
+ // Load the user type FXML
+ FXMLLoader loader = new FXMLLoader(getClass().getResource(fxmlPath));
+ Parent userTypeRoot = loader.load();
+
+ // Get current stage
+ Stage currentStage = (Stage) Id_number.getScene().getWindow();
+
+ // Create new scene and set it
+ Scene userTypeScene = new Scene(userTypeRoot);
+ currentStage.setScene(userTypeScene);
+ currentStage.setTitle(windowTitle);
+
+ } catch (IOException e) {
+ showAlert("Error", "Could not load " + userType + " dashboard: " + e.getMessage());
+ }
