 // Create new scene and set it
 Scene signUpScene = new Scene(signUpRoot);
 currentStage.setScene(signUpScene);
 currentStage.setTitle("BSEC Sign Up");

 } catch (IOException e) {
 showAlert("Error", "Could not load Sign Up page: " + e.getMessage());
 }
 }

